import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { tap } from 'rxjs';
import { environment } from 'src/environments/environment.development';
import { User } from '../models/login-form-value.model';
@Injectable({
  providedIn: 'root'
})
export class AuthService {

  constructor(private http: HttpClient) { }
  onRegister(name: string, email: string, password: string, role: string) {
    return this.http.post(`${environment.apiUrl}/user`,{name, email, password, role}).subscribe(
      (res: any) => {
        console.log(res)
      }
    )
  }
  onLogin(email: string, password: string) {
    return this.http.post(`${environment.apiUrl}/user/login`,{email, password}).subscribe(
      (res: any) => {
        console.log(res)
        sessionStorage.setItem('token', res.token)
      }
    )
  }
}
