import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { UserModel } from '../../models/user.model';
import { MainService } from '../../services/main.service';
import { MatTableDataSource } from '@angular/material/table';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {

  displayedColumns: string[] = [
    '_id',
    'name',
    'email',
    'password',
    'role'
  ];
  loading$!: Observable<boolean>;
  users$!: Observable<UserModel[]>;
  dataSource = new MatTableDataSource(this.users$);

  constructor(private mainService: MainService){}
  //@ts-ignore
  ngOnInit(): void {
    this.initObservables();
    this.mainService.getUsers();
  }
  private initObservables(){
    this.loading$ = this.mainService.loading$;
    this.users$ = this.mainService.user$
  }
}
<mat-spinner *ngIf="loading$ | async" style="margin-left: 40%"></mat-spinner>
  <mat-nav-list *ngIf="users$ | async as users">
    <a *ngFor="let user of users" style="border: 1px solid black;">
      <p >{{ user._id }}</p>
      <p >{{ user.name }}</p>
      <p >{{ user.email }}</p>
      <p >{{ user.password }}</p>
      <p >{{ user.role }}</p>
    </a>
  </mat-nav-list>
